import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Scores.css';
import NewsSidebar from './NewsSidebar';

function Scores() {
  const { sport, league } = useParams();
  const navigate = useNavigate();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const scoreboardBaseUrl = `https://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`;

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const response = await fetch(scoreboardBaseUrl);
        if (!response.ok) throw new Error('Network response was not ok');
        const jsonData = await response.json();
        setData(jsonData);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [scoreboardBaseUrl]);

  return (
    <div className="Scores-container">
      <div className="Scores">
        <button onClick={() => navigate('/')} className="home-button">Go to Home</button>
        <h2>{league.toUpperCase()} Scoreboard</h2>

        {loading && <div className="loading">Loading...</div>}
        {error && <div className="error">Error: {error.message}</div>}

        {data && (
          <div className="scoreboard">
            {data.events.length > 0 ? (
              data.events.map((event, index) => {
                const homeCompetitor = event.competitions[0].competitors.find(team => team.homeAway === 'home');
                const awayCompetitor = event.competitions[0].competitors.find(team => team.homeAway === 'away');

                if (!homeCompetitor || !awayCompetitor) return null;

                const homeTeam = homeCompetitor.team;
                const awayTeam = awayCompetitor.team;
                const homeScore = homeCompetitor.score || '0';
                const awayScore = awayCompetitor.score || '0';

                const startTime = event.competitions[0].date
                  ? new Date(event.competitions[0].date).toLocaleString()
                  : 'Not Available';

                const period = event.status?.period || 'Not Available';
                const clock = event.status?.displayClock || 'Not Available';

                const homeScoreBold = homeScore > awayScore ? 'bold' : '';
                const awayScoreBold = awayScore > homeScore ? 'bold' : '';

                // Betting Odds with proper checks
                const odds = event.odds?.[0] || null;
                const homeTeamOdds = odds?.homeTeamOdds || null;
                const awayTeamOdds = odds?.awayTeamOdds || null;

                return (
                  <div key={index} className="game">
                    <h3>{homeTeam.displayName} vs {awayTeam.displayName}</h3>
                    <p>Start Time: {startTime}</p>
                    <div className="scores">
                      <p>
                        <span className="team-name">{homeTeam.displayName}</span>
                        <span className={`score ${homeScoreBold}`}>{homeScore}</span>
                      </p>
                      <p>
                        <span className="team-name">{awayTeam.displayName}</span>
                        <span className={`score ${awayScoreBold}`}>{awayScore}</span>
                      </p>
                    </div>
                    <div className="status">
                      <p>Period: {period} | Time: {clock}</p>
                    </div>

                    {/* Betting Odds Section */}
                    {odds && (
                      <div className="odds">
                        <h4>Betting Odds:</h4>
                        <p>
                          <strong>Spread:</strong> {odds.spread ?? 'N/A'} |
                          <strong> Over/Under:</strong> {odds.overUnder ?? 'N/A'}
                        </p>
                        <div className="odds-teams">
                          {homeTeamOdds && (
                            <div className="odds-team">
                              {homeTeamOdds.team.logo && (
                                <img src={homeTeamOdds.team.logo} alt={homeTeamOdds.team.name} width="25" />
                              )}
                              <p>
                                {homeTeamOdds.team.displayName}: {homeTeamOdds.favorite ? 'Favorite' : 'Underdog'}
                              </p>
                            </div>
                          )}
                          {awayTeamOdds && (
                            <div className="odds-team">
                              {awayTeamOdds.team.logo && (
                                <img src={awayTeamOdds.team.logo} alt={awayTeamOdds.team.name} width="25" />
                              )}
                              <p>
                                {awayTeamOdds.team.displayName}: {awayTeamOdds.favorite ? 'Favorite' : 'Underdog'}
                              </p>
                            </div>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                );
              })
            ) : (
              <p>No scores available.</p>
            )}
          </div>
        )}
      </div>

      <NewsSidebar sport={sport} league={league} />
    </div>
  );
}

export default Scores;
