import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Scores.css';
import NewsSidebar from './NewsSidebar'; // Import NewsSidebar component

function Scores() {
  const { sport, league } = useParams(); // Get sport and league from the URL
  const navigate = useNavigate();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Construct the API URL dynamically based on sport and league
  const scoreboardBaseUrl = `https://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`;

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const response = await fetch(scoreboardBaseUrl);
        if (!response.ok) throw new Error('Network response was not ok');
        const jsonData = await response.json();
        console.log(jsonData); // Log the full response to see the structure
        setData(jsonData);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [scoreboardBaseUrl]);

  return (
    <div className="Scores-container">
      <div className="Scores">
        <button onClick={() => navigate('/')} className="home-button">Go to Home</button>

        <h2>{league.toUpperCase()} Scoreboard</h2>

        {loading && <div className="loading">Loading...</div>}
        {error && <div className="error">Error: {error.message}</div>}

        {data && (
          <>
            {/* Scoreboard Section */}
            <div className="scoreboard">
              {data.events.length > 0 ? (
                data.events.map((event, index) => {
                  // Extracting team and score data
                  const homeTeam = event.competitions[0].competitors.find(team => team.homeAway === 'home').team;
                  const awayTeam = event.competitions[0].competitors.find(team => team.homeAway === 'away').team;

                  const homeScore = event.competitions[0].competitors.find(team => team.homeAway === 'home').score;
                  const awayScore = event.competitions[0].competitors.find(team => team.homeAway === 'away').score;

                  const startTime = event.competitions[0].date
                    ? new Date(event.competitions[0].date).toLocaleString()
                    : 'Not Available';

                  const homeScoreBold = homeScore > awayScore ? 'bold' : '';
                  const awayScoreBold = awayScore > homeScore ? 'bold' : '';

                  const statusDetail = event.status ? event.status.type.detail : 'No additional details available';

                  // Odds and spread/overUnder extraction
                  const odds = event.odds && event.odds.length > 0 ? event.odds[0] : null;
                  const spread = odds ? odds.spread : 'Not Available';
                  const overUnder = odds ? odds.overUnder : 'Not Available';
                  const homeTeamOdds = odds ? odds.homeTeamOdds : {};
                  const awayTeamOdds = odds ? odds.awayTeamOdds : {};

                  // Build scoreboard for the event
                  return (
                    <div className="scoreboard-event" key={index}>
                      <div className="team-info">
                        <div className="team">
                          <img src={homeTeam.logo} alt={homeTeam.displayName} className="team-logo" />
                          <p>{homeTeam.displayName}</p>
                          <p className={homeScoreBold}>{homeScore}</p>
                        </div>
                        <div className="vs">VS</div>
                        <div className="team">
                          <img src={awayTeam.logo} alt={awayTeam.displayName} className="team-logo" />
                          <p>{awayTeam.displayName}</p>
                          <p className={awayScoreBold}>{awayScore}</p>
                        </div>
                      </div>

                      <div className="status-info">
                        <p>Status: {statusDetail}</p>
                        <p>Start Time: {startTime}</p>
                        <p>Spread: {spread}</p>
                        <p>Over/Under: {overUnder}</p>
                        <p>Home Team Odds: {homeTeamOdds.favorite ? 'Favorite' : 'Underdog'}</p>
                        <p>Away Team Odds: {awayTeamOdds.favorite ? 'Favorite' : 'Underdog'}</p>
                      </div>
                    </div>
                  );
                })
              ) : (
                <div>No games found</div>
              )}
            </div>
          </>
        )}
      </div>

      {/* Sidebar */}
      <NewsSidebar />
    </div>
  );
}

export default Scores;
