import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Scores.css';
import NewsSidebar from './NewsSidebar'; // Import NewsSidebar component

function Scores() {
  const { sport, league } = useParams(); // Get sport and league from the URL
  const navigate = useNavigate();
  const [data, setData] = useState(null);
  const [oddsData, setOddsData] = useState(null);  // New state for odds
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Construct the API URL dynamically based on sport and league
  const scoreboardBaseUrl = `https://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`;

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const response = await fetch(scoreboardBaseUrl);
        if (!response.ok) throw new Error('Network response was not ok');
        const jsonData = await response.json();
        setData(jsonData);

        // Fetch odds data (adjust based on your available API or source)
        const oddsResponse = await fetch(`https://someapi.com/odds/${sport}/${league}`);
        if (oddsResponse.ok) {
          const oddsJson = await oddsResponse.json();
          setOddsData(oddsJson);
        }

      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [scoreboardBaseUrl, sport, league]); // Add 'sport' and 'league' here


  return (
    <div className="Scores-container">
      <div className="Scores">
        <button onClick={() => navigate('/')} className="home-button">Go to Home</button>

        <h2>{league.toUpperCase()} Scoreboard</h2>

        {loading && <div className="loading">Loading...</div>}
        {error && <div className="error">Error: {error.message}</div>}

        {/* Display games if available */}
        {data && data.events && data.events.length > 0 ? (
          data.events.map((event, index) => {
            const homeTeam = event.competitions[0].competitors.find(team => team.homeAway === 'home');
            const awayTeam = event.competitions[0].competitors.find(team => team.homeAway === 'away');

            return (
              <div key={index} className="game-details">
                <div className="team-info">
                  <h3>{homeTeam.team.displayName} vs {awayTeam.team.displayName}</h3>
                  <div className="score">
                    <span>{homeTeam.score} - {awayTeam.score}</span>
                  </div>
                </div>

                {/* Team statistics */}
                <div className="team-stats">
                  <h4>{homeTeam.team.displayName} Stats:</h4>
                  <ul>
                    <li>Rebounds: {homeTeam.statistics.find(stat => stat.name === 'rebounds')?.displayValue}</li>
                    <li>Assists: {homeTeam.statistics.find(stat => stat.name === 'assists')?.displayValue}</li>
                    <li>Field Goals: {homeTeam.statistics.find(stat => stat.name === 'fieldGoalsMade')?.displayValue}/{homeTeam.statistics.find(stat => stat.name === 'fieldGoalsAttempted')?.displayValue}</li>
                    <li>Three Point FG: {homeTeam.statistics.find(stat => stat.name === 'threePointFieldGoalsMade')?.displayValue}</li>
                  </ul>
                </div>

                <div className="team-stats">
                  <h4>{awayTeam.team.displayName} Stats:</h4>
                  <ul>
                    <li>Rebounds: {awayTeam.statistics.find(stat => stat.name === 'rebounds')?.displayValue}</li>
                    <li>Assists: {awayTeam.statistics.find(stat => stat.name === 'assists')?.displayValue}</li>
                    <li>Field Goals: {awayTeam.statistics.find(stat => stat.name === 'fieldGoalsMade')?.displayValue}/{awayTeam.statistics.find(stat => stat.name === 'fieldGoalsAttempted')?.displayValue}</li>
                    <li>Three Point FG: {awayTeam.statistics.find(stat => stat.name === 'threePointFieldGoalsMade')?.displayValue}</li>
                  </ul>
                </div>

                {/* Display Odds if available */}
                {oddsData && oddsData[event.id] && (
                  <div className="odds-section">
                    <h5>Betting Odds:</h5>
                    <p>Home Team Odds: {oddsData[event.id].homeOdds}</p>
                    <p>Away Team Odds: {oddsData[event.id].awayOdds}</p>
                  </div>
                )}

                {/* Show team records */}
                <div className="team-records">
                  <h5>Team Records:</h5>
                  <div>
                    <strong>{homeTeam.team.displayName}</strong>: {homeTeam.team.record.summary}
                  </div>
                  <div>
                    <strong>{awayTeam.team.displayName}</strong>: {awayTeam.team.record.summary}
                  </div>
                </div>
              </div>
            );
          })
        ) : (
          <div>No games available at the moment.</div>
        )}
      </div>

      {/* Sidebar for news */}
      <NewsSidebar />
    </div>
  );
}

export default Scores;
