// Scoreboard.js or GameScoreboard.js

import React, { useState } from 'react';
import './Scoreboard.css'; // Make sure to import the CSS file
import './Scoreboard.css';

function Scoreboard({ gameData }) {
  const [showHomeStats, setShowHomeStats] = useState(false);
  const [showAwayStats, setShowAwayStats] = useState(false);

  const toggleHomeStats = () => setShowHomeStats(!showHomeStats);
  const toggleAwayStats = () => setShowAwayStats(!showAwayStats);

  const homeTeam = gameData.homeTeam;
  const awayTeam = gameData.awayTeam;
  const homeScore = gameData.homeScore;
  const awayScore = gameData.awayScore;
  const startTime = gameData.startTime;
  const status = gameData.status;

  // Stats filtering logic (just an example, depending on your API)
  const filteredHomeStats = homeTeam.stats;  // Replace with actual stats data
  const filteredAwayStats = awayTeam.stats;  // Replace with actual stats data

  return (
    <div className="game">
      <div className="game-header">
        <div className="team-names">
          <span className="team-name home">{homeTeam.displayName}</span>
          <span className="vs">vs</span>
          <span className="team-name away">{awayTeam.displayName}</span>
        </div>
        <div className="game-time">
          <span>{startTime}</span>
          <span className="status">{status}</span>
        </div>
      </div>

      <div className="game-score">
        <div className="score home">
          <span>{homeScore}</span>
        </div>
        <div className="score away">
          <span>{awayScore}</span>
        </div>
      </div>

      {/* Team Stats Dropdown */}
      <div className="team-stats">
        <button onClick={toggleHomeStats} className="toggle-stats">
          {showHomeStats ? "Hide" : "Show"} {homeTeam.displayName} Stats
        </button>
        {showHomeStats && (
          <ul>
            {filteredHomeStats.map((stat, i) => (
              <li key={i}>
                {stat.name}: {stat.displayValue}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="team-stats">
        <button onClick={toggleAwayStats} className="toggle-stats">
          {showAwayStats ? "Hide" : "Show"} {awayTeam.displayName} Stats
        </button>
        {showAwayStats && (
          <ul>
            {filteredAwayStats.map((stat, i) => (
              <li key={i}>
                {stat.name}: {stat.displayValue}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Scoreboard;
