import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function Scores() {
  const { sport, league } = useParams(); // Get sport and league from the URL
  const navigate = useNavigate();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showHomeStats, setShowHomeStats] = useState({}); // State to manage dropdown visibility for home team stats
  const [showAwayStats, setShowAwayStats] = useState({}); // State to manage dropdown visibility for away team stats

  // Construct the API URL dynamically based on sport and league
  const scoreboardBaseUrl = `https://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`;

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const response = await fetch(scoreboardBaseUrl);

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const jsonData = await response.json();
        setData(jsonData);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [scoreboardBaseUrl]);

  const toggleHomeStats = (index) => {
    setShowHomeStats((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  const toggleAwayStats = (index) => {
    setShowAwayStats((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  // Function to filter relevant stats
  const getRelevantStats = (stats) => {
    const relevantStats = [
      "fieldGoalPct",
      "threePointPct",
      "freeThrowPct",
      "assists",
      "turnovers"
    ];

    return stats.filter(stat => relevantStats.includes(stat.name));
  };

  return (
    <div className="Scores">
      <button onClick={() => navigate('/')} className="home-button">Go to Home</button>

      <h2>{league.toUpperCase()} Scoreboard</h2>
      {loading && <div>Loading...</div>}
      {error && <div style={{ color: 'red' }}>Error: {error.message}</div>}
      {data && (
        <div className="scoreboard">
          {data.events.length > 0 ? (
            data.events.map((event, index) => {
              const homeTeam = event.competitions[0].competitors.find(team => team.homeAway === 'home').team;
              const awayTeam = event.competitions[0].competitors.find(team => team.homeAway === 'away').team;

              const homeScore = event.competitions[0].competitors.find(team => team.homeAway === 'home').score;
              const awayScore = event.competitions[0].competitors.find(team => team.homeAway === 'away').score;

              const homeStats = event.competitions[0].competitors.find(team => team.homeAway === 'home').statistics;
              const awayStats = event.competitions[0].competitors.find(team => team.homeAway === 'away').statistics;

              const startTime = event.competitions[0].date
                ? new Date(event.competitions[0].date).toLocaleString()
                : 'Not Available';
              const status = event.status ? event.status.description : 'Not Available';

              // Filter relevant stats
              const filteredHomeStats = getRelevantStats(homeStats);
              const filteredAwayStats = getRelevantStats(awayStats);

              return (
                <div key={index} className="game">
                  <h3>{homeTeam.displayName} vs {awayTeam.displayName}</h3>
                  <p>Start Time: {startTime}</p>
                  <p>Status: {status}</p>
                  <p>Score: {homeTeam.displayName} {homeScore} - {awayTeam.displayName} {awayScore}</p>

                  {/* Home Team Stats Dropdown */}
                  <div className="team-stats">
                    <button onClick={() => toggleHomeStats(index)}>
                      {showHomeStats[index] ? "Hide" : "Show"} {homeTeam.displayName} Stats
                    </button>
                    {showHomeStats[index] && (
                      <ul>
                        {filteredHomeStats.map((stat, i) => (
                          <li key={i}>
                            {stat.name}: {stat.displayValue}
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>

                  {/* Away Team Stats Dropdown */}
                  <div className="team-stats">
                    <button onClick={() => toggleAwayStats(index)}>
                      {showAwayStats[index] ? "Hide" : "Show"} {awayTeam.displayName} Stats
                    </button>
                    {showAwayStats[index] && (
                      <ul>
                        {filteredAwayStats.map((stat, i) => (
                          <li key={i}>
                            {stat.name}: {stat.displayValue}
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                </div>
              );
            })
          ) : (
            <p>No scores available.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default Scores;
