import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css'; // Ensure this file exists and is styled

function Home() {
  const navigate = useNavigate();

  const sports = [
    { name: "NBA Basketball", value: "basketball_nba", league: "nba", image: "/images/nba.jpg" },
    { name: "College Basketball", value: "basketball_ncaab", league: "college", image: "/images/college-basketball.png" },
    { name: "NFL Football", value: "americanfootball_nfl", league: "nfl", image: "/images/nfl.png" },
    { name: "MLB Baseball", value: "baseball_mlb", league: "mlb", image: "/images/mlb.png" },
    { name: "NHL Hockey", value: "icehockey_nhl", league: "nhl", image: "/images/nhl.png" }
  ];

  return (
    <div className="Home">
      <header className="App-header">
        <div className="sports-tabs">
          {sports.map((sport) => (
            <button
              key={sport.value}
              className="sport-tab"
              onClick={() => navigate(`/scores/${sport.value}/${sport.league}`)}
            >
              <img src={sport.image} alt={sport.name} className="sport-icon" />
              {sport.name}
            </button>
          ))}
        </div>
      </header>

      {/* Main Image */}
      <div className="main-image">
        <img src="/images/sports-banner.jpeg" alt="Sports Odds" />
      </div>
    </div>
  );
}

export default Home;
