import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Scores.css';

function Scores() {
  const { sport, league } = useParams();
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const scoresUrl = `https://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`;

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const response = await fetch(scoresUrl);
        if (!response.ok) {
          throw new Error('Failed to fetch scores');
        }
        const data = await response.json();
        setScores(data.events || []);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [scoresUrl]);

  return (
    <div className="scores-container">
      <h3>Live {league ? league.toUpperCase() : 'Sports'} Scores</h3>
      {loading && <p className="loading">Loading...</p>}
      {error && <p className="error">Error: {error.message}</p>}
      <ul>
        {scores.map((game, index) => (
          <li key={index} className="score-item">
            <div className="team-names">
              <span className="team">{game.competitions[0].competitors[0].team.displayName}</span>
              <span className="score">{game.competitions[0].competitors[0].score}</span>
            </div>
            <div className="team-names">
              <span className="team">{game.competitions[0].competitors[1].team.displayName}</span>
              <span className="score">{game.competitions[0].competitors[1].score}</span>
            </div>
            <span className="status">{game.status.type.description}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Scores;
