// Scores.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './Scores.css';

function Scores() {
  const { sport, league } = useParams(); // Get sport and league from the URL
  const [scores, setScores] = useState([]);

  useEffect(() => {
    async function fetchScores() {
      try {
        const response = await fetch(`https://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`);
        const data = await response.json();
        setScores(data.events); // Assuming the events array contains the game data
      } catch (error) {
        console.error("Error fetching scores:", error);
      }
    }

    fetchScores();
  }, [sport, league]);

  return (
    <div className="scores">
      <h2>Live Scores and Odds for {sport} - {league}</h2>
      <div className="scores-grid">
        {scores.map((game) => (
          <div key={game.id} className="game-card">
            <div className="game-details">
              <h3>{game.shortName}</h3>
              <p>{game.status.type.description}</p>
              <div>
                <span>{game.competitions[0].competitors[0].team.displayName}</span>
                <span> vs. </span>
                <span>{game.competitions[0].competitors[1].team.displayName}</span>
              </div>
              <div>
                <span>Score: {game.competitions[0].competitors[0].score} - {game.competitions[0].competitors[1].score}</span>
              </div>
            </div>
            {/* Display Odds */}
            <div className="odds">
              <div>
                <span>{game.odds[0].provider.name}:</span>
                <span> Spread: {game.odds[0].spread} | Over/Under: {game.odds[0].overUnder}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Scores;
