import React, { useState, useEffect } from 'react';
import NewsSidebar from './NewsSidebar'; // Import if needed

const Scores = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const scoreboardBaseUrl = 'https://api.sportsdata.io/v3/sports/scores/json/GamesBySeason/2022/';

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const response = await fetch(scoreboardBaseUrl);
        if (!response.ok) throw new Error('Network response was not ok');
        const jsonData = await response.json();
        console.log(jsonData); // Log the data for debugging
        setData(jsonData);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [scoreboardBaseUrl]);

  return (
    <div className="Scores-container">
      <div className="Scores">
        {loading && <p>Loading...</p>}
        {error && <p>Error loading data: {error.message}</p>}
        {data.map((game, index) => (
          <div className="game" key={index}>
            <h3>{game.homeTeam.name} vs {game.awayTeam.name}</h3>
            <div className="scores">
              <p>
                <span className="team-name">{game.homeTeam.name}</span>
                <span className="score">{game.homeTeam.score}</span>
              </p>
              <p>
                <span className="team-name">{game.awayTeam.name}</span>
                <span className="score">{game.awayTeam.score}</span>
              </p>
            </div>
          </div>
        ))}
      </div>

      {/* Include NewsSidebar if defined and needed */}
      {false && <NewsSidebar />}  {/* Replace with your condition for showing NewsSidebar */}
    </div>
  );
};

export default Scores;
