import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './Scores.css';
import NewsSidebar from './NewsSidebar';

function Scores() {
  const { sport, league } = useParams();
  const navigate = useNavigate();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchScores = async () => {
      const scoreboardBaseUrl = `https://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`;

      try {
        const response = await fetch(scoreboardBaseUrl);
        if (!response.ok) throw new Error('Network response was not ok');
        const jsonData = await response.json();
        setData(jsonData);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [sport, league]);

  return (
    <div className="Scores-container">
      <div className="Scores">
        <button onClick={() => navigate('/')} className="home-button">Go to Home</button>

        <h2>{league.toUpperCase()} Scoreboard</h2>

        {loading && <div className="loading">Loading...</div>}
        {error && <div className="error">Error: {error.message}</div>}

        {data?.events?.length > 0 ? (
          <div className="scoreboard">
            {data.events.map((event, index) => {
              const homeCompetitor = event.competitions?.[0]?.competitors?.find(team => team.homeAway === 'home');
              const awayCompetitor = event.competitions?.[0]?.competitors?.find(team => team.homeAway === 'away');

              if (!homeCompetitor || !awayCompetitor) return null; // Skip rendering if data is missing

              const homeTeam = homeCompetitor.team;
              const awayTeam = awayCompetitor.team;

              const homeScore = homeCompetitor.score || 'N/A';
              const awayScore = awayCompetitor.score || 'N/A';

              // Extract team records (win-loss summary)
              const homeRecord = homeTeam?.records?.find(rec => rec.name === 'overall')?.summary || 'N/A';
              const awayRecord = awayTeam?.records?.find(rec => rec.name === 'overall')?.summary || 'N/A';

              return (
                <div key={index} className="game">
                  <h3>
                    <div className="team">
                      <img src={homeTeam.logo} alt={homeTeam.displayName} width="30" />
                      <span>{homeTeam.displayName} ({homeRecord})</span>
                    </div>
                    <span> vs </span>
                    <div className="team">
                      <img src={awayTeam.logo} alt={awayTeam.displayName} width="30" />
                      <span>{awayTeam.displayName} ({awayRecord})</span>
                    </div>
                  </h3>
                  <div className="scores">
                    <p>
                      <span className="team-name">{homeTeam.displayName}</span>
                      <span className={`score ${homeScore > awayScore ? 'bold' : ''}`}>{homeScore}</span>
                    </p>
                    <p>
                      <span className="team-name">{awayTeam.displayName}</span>
                      <span className={`score ${awayScore > homeScore ? 'bold' : ''}`}>{awayScore}</span>
                    </p>
                  </div>
                  <div className="status">
                    <p><strong>{event.status?.type?.detail || 'No additional details available'}</strong></p>
                  </div>
                </div>
              );
            })}
          </div>
        ) : (
          <p>No scores available.</p>
        )}
      </div>

      <NewsSidebar sport={sport} league={league} />
    </div>
  );
}

export default Scores;
