import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function Scores() {
  const { sport, league } = useParams(); // Get sport and league from URL params
  const navigate = useNavigate();
  const [news, setNews] = useState(null);
  const [scores, setScores] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // API URLs for ESPN news and scoreboard
  const newsUrl = `http://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/news`;
  const scoreboardUrl = `http://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`;

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch both news and scoreboard data
        const newsResponse = await fetch(newsUrl);
        const scoreboardResponse = await fetch(scoreboardUrl);

        if (!newsResponse.ok || !scoreboardResponse.ok) {
          throw new Error('Error fetching data from ESPN');
        }

        // Parse JSON data
        const newsData = await newsResponse.json();
        const scoreboardData = await scoreboardResponse.json();

        // Set the data into state
        setNews(newsData.articles);
        setScores(scoreboardData.events);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (sport && league) {
      fetchData();
    }
  }, [sport, league]);

  return (
    <div className="Scores">
      <button onClick={() => navigate('/')} className="home-button">Go to Home</button>

      <h2>{sport.replace('_', ' ').toUpperCase()} - {league.toUpperCase()} News & Scoreboard</h2>

      {loading && <div>Loading...</div>}
      {error && <div style={{ color: 'red' }}>Error: {error}</div>}

      {/* News Section */}
      <div className="news-section">
        <h3>News</h3>
        {news ? (
          <ul>
            {news.map((article, index) => (
              <li key={index}>
                <a href={article.links.web.href} target="_blank" rel="noopener noreferrer">
                  {article.headline}
                </a>
              </li>
            ))}
          </ul>
        ) : (
          <p>No news available.</p>
        )}
      </div>

      {/* Scoreboard Section */}
      <div className="scoreboard-section">
        <h3>Scoreboard</h3>
        {scores ? (
          <div className="scoreboard">
            {scores.map((event, index) => (
              <div key={index} className="game">
                <h4>{event.homeTeam.name} vs {event.awayTeam.name}</h4>
                <p>Start Time: {new Date(event.date).toLocaleString()}</p>
                {event.status && <p>Status: {event.status.description}</p>}
              </div>
            ))}
          </div>
        ) : (
          <p>No games scheduled.</p>
        )}
      </div>
    </div>
  );
}

export default Scores;
