{data.events.length > 0 ? (
  data.events.map((event, index) => {
    const competition = event.competitions[0];

    const homeTeam = competition.competitors.find(team => team.homeAway === 'home').team;
    const awayTeam = competition.competitors.find(team => team.homeAway === 'away').team;

    const homeScore = competition.competitors.find(team => team.homeAway === 'home').score;
    const awayScore = competition.competitors.find(team => team.homeAway === 'away').score;

    const homeScoreBold = homeScore > awayScore ? 'bold' : '';
    const awayScoreBold = awayScore > homeScore ? 'bold' : '';

    const statusDetail = event.status ? event.status.type.detail : 'No additional details available';

    // Extract odds if available
    const odds = competition.odds ? competition.odds[0] : null;
    const homeTeamOdds = odds ? odds.homeTeamOdds : null;
    const awayTeamOdds = odds ? odds.awayTeamOdds : null;
    const overUnder = odds ? odds.overUnder : null;

    return (
      <div key={index} className="game">
        <h3>
          <div className="team">
            <img src={homeTeam.logo} alt={homeTeam.displayName} width="30" />
            <span>{homeTeam.displayName}</span>
          </div>
          <span> vs </span>
          <div className="team">
            <img src={awayTeam.logo} alt={awayTeam.displayName} width="30" />
            <span>{awayTeam.displayName}</span>
          </div>
        </h3>
        <div className="scores">
          <p>
            <span className="team-name">{homeTeam.displayName}</span>
            <span className={`score ${homeScoreBold}`}>{homeScore}</span>
            {homeTeamOdds && (
              <span className="odds">({homeTeamOdds.favorite ? 'Fav' : 'Underdog'}, Spread: {odds.spread})</span>
            )}
          </p>
          <p>
            <span className="team-name">{awayTeam.displayName}</span>
            <span className={`score ${awayScoreBold}`}>{awayScore}</span>
            {awayTeamOdds && (
              <span className="odds">({awayTeamOdds.favorite ? 'Fav' : 'Underdog'}, Spread: {odds.spread})</span>
            )}
          </p>
        </div>
        {overUnder && (
          <div className="odds-info">
            <p><strong>Over/Under: {overUnder}</strong></p>
          </div>
        )}
        <div className="status">
          <p><strong>{statusDetail}</strong></p> 
        </div>
      </div>
    );
  })
) : (
  <p>No scores available.</p>
)}
