import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function Scores() {
  const { sport, league } = useParams(); // Get sport and league from the URL
  const navigate = useNavigate();
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Construct the API URL dynamically based on sport and league
  const scoreboardBaseUrl = `https://site.api.espn.com/apis/site/v2/sports/${sport}/${league}/scoreboard`;

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const response = await fetch(scoreboardBaseUrl);

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const jsonData = await response.json();
        setData(jsonData.events);  // Assuming `events` contains the actual games
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, [scoreboardBaseUrl]); // Added scoreboardBaseUrl as a dependency

  return (
    <div className="Scores">
      <button onClick={() => navigate('/')} className="home-button">Go to Home</button>

      <h2>{league.toUpperCase()} Scoreboard</h2>
      {loading && <div>Loading...</div>}
      {error && <div style={{ color: 'red' }}>Error: {error.message}</div>}
      {data && (
        <div className="scoreboard">
          {data.length > 0 ? (
            data.map((event, index) => (
              <div key={index} className="game">
                <h3>
                  {event.competitions[0].competitors[0].team.displayName} vs{' '}
                  {event.competitions[0].competitors[1].team.displayName}
                </h3>
                {event.commenceTime ? (
                  <p>Start Time: {new Date(event.commenceTime).toLocaleString()}</p>
                ) : (
                  <p>Start Time: Not Available</p>
                )}
                {event.status && event.status.description ? (
                  <p>Status: {event.status.description}</p>
                ) : (
                  <p>Status: Not Available</p>
                )}
              </div>
            ))
          ) : (
            <p>No scores available.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default Scores;
